import React, { useState } from "react";
import "./Second.scss";
import Course from "./Course/Course";
import MediaImage from "../../../global/assets/media.svg";
import MusicImage from "../../../global/assets/music2.svg";
import ImageCourseImage from "../../../global/assets/image_course.svg";

interface SecondContentProps {}

interface CourseDetailsProps {
    title: string;
    article: string;
}

const Second: React.FC<SecondContentProps> = (props) => {
    const [selectedCourse, setSelectedCourse] = useState(null);

    const handleCourseClick = (event, courseData) => {
        setSelectedCourse(courseData);
    };

    const showCourseDetails = () => {
        if (!selectedCourse) return;

        const courseDetails = (
            <div className={"CourseDetails"}>
                <h2>{selectedCourse.title}</h2>
                <p>{selectedCourse.article}</p>
            </div>
        );

        const popupElement = document.createElement("div");
        popupElement.innerHTML = courseDetails;

        document.body.appendChild(popupElement);

        const popupCloseButton = popupElement.querySelector(".close-button");
        popupCloseButton.addEventListener("click", () => {
            document.body.removeChild(popupElement);
        });
    };

    const coursesData = [
        { id: 1, title: "Media", image: MediaImage, article: "Media course article" },
        { id: 2, title: "Music", image: MusicImage, article: "Music course article" },
        { id: 3, title: "Image", image: ImageCourseImage, article: "Image course article" },
    ];

    return (
        <div className={"MainCourses"}>
            <h1>Our courses</h1>
            <div className={"Courses"}>
                {coursesData.map((course) => (
                    <Course
                        key={course.id}
                        title={course.title}
                        image={course.image}
                        onClick={(event) => handleCourseClick(event, course)}
                    />
                ))}
            </div>
            <button onClick={showCourseDetails}>Show Course Details</button>
        </div>
    );
};

export default Second;
