// Game.tsx
import React, { useState } from 'react';
import { useDrop } from 'react-dnd';
import { TextBlock } from './TextBlock';
import { BlockTypes } from './Types';

interface TextBlockData {
    id: number;
    type: string;
}

interface GameProps {
    blocks: TextBlockData[];
}

const Game: React.FC<GameProps> = ({ blocks }) => {
    const [droppedBlocks, setDroppedBlocks] = useState<number[]>([]);

    const handleDrop = (blockId: number) => {
        setDroppedBlocks((prevBlocks) => [...prevBlocks, blockId]);
    };

    return (
        <div>
            {blocks.map((block) => (
                <TextBlock
                    key={block.id}
                    id={block.id}
                    text={`Block ${block.id}`}
                    type={block.type}
                    isDropped={droppedBlocks.includes(block.id)}
                    isAnimating={false} // Change this based on your animation logic
                />
            ))}

            <div className="drop-area">
                <div>
                    {droppedBlocks.length > 0 && (
                        <p>Dropped Blocks: {droppedBlocks.map((id) => `Block ${id}`).join(', ')}</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Game;
