import React, { useState } from 'react';
import "./Course.scss";
import CourseDetails from './../CourseDetails/CourseDetails';
import MediaImage from "../../../global/assets/media.svg";
import MusicImage from "../../../global/assets/music2.svg";
import ImageCourseImage from "../../../global/assets/image_course.svg";

interface CourseProps {
    title: string;
    image: any;
    courseDetails: CourseDetailsProps | null;
    handleCourseDetails: (newCourseDetails: CourseDetailsProps) => void;
}

const Course: React.FC<CourseProps> = (props) => {
    const { title, image, courseDetails, handleCourseDetails } = props;

    // Update the course data from the parent component
    if (courseDetails) {
        // Get the course title and article from the course details prop
        const courseTitle = courseDetails.title || title;
        const courseArticle = courseDetails.article || null;

        // Update the title and article props with the course data
        props.title = courseTitle;
        props.article = courseArticle;
    }

    const handleClick = () => {
        if (courseDetails) {
            // Pass the updated course details to the parent component
            handleCourseDetails(courseDetails);
        } else {
            // Set the courseDetails prop to null to indicate that no course details are available
            props.courseDetails = null;
            props.title = title;
            props.article = null;
        }
    };

    return (
        <div
            className={`Course`}
        >
            <div className={`image-container`}>
                <img src={image} alt={title} />
            </div>
            <h2>{title}</h2>

            <button onClick={handleClick}>{courseDetails ? 'Open Course Details' : 'Show Course Details'}</button>
        </div>
    );
};

export default Course;
