import React, { useState } from 'react';
import './PopUpContent.scss';

const PopUpContent = () => {
    const [currentSlide, setCurrentSlide] = useState(0);
    // Additional state to handle interactive elements
    const [selectedOption, setSelectedOption] = useState(null);

    const theorySlides = [
        // ... your theory slides ...
    ];

    const practicalSlides = [
        {
            type: "Practical",
            title: "Quiz: Identify the Tools",
            content: "What tool is used for cutting clips?",
            exercise: {
                type: "quiz",
                options: ["Razor", "Hand", "Pen Tool"],
                correctAnswer: "Razor"
            }
        },
        // ... more practical slides ...
    ];

    const conclusionSlide = {
        type: "Conclusion",
        title: "Summary of Key Concepts",
        content: "This is a summary of the key points covered in the course."
    };

    const slides = [...theorySlides, ...practicalSlides, conclusionSlide];

    // Handle selecting an option in a quiz
    const handleOptionSelect = (option) => {
        setSelectedOption(option);
    };

    const goToNextSlide = () => {
        setCurrentSlide(currentSlide + 1);
        setSelectedOption(null); // Reset for the next slide
    };

    const goToPreviousSlide = () => {
        setCurrentSlide(currentSlide - 1);
        setSelectedOption(null); // Reset for the next slide
    };

    const renderSlideContent = () => {
        const slide = slides[currentSlide];
        const isQuiz = slide.type === 'Practical' && slide.exercise?.type === 'quiz';

        return (
            <div>
                <p>{slide.content}</p>
                {isQuiz && (
                    <div>
                        {slide.exercise.options.map((option, index) => (
                            <button key={index} onClick={() => handleOptionSelect(option)}>
                                {option}
                            </button>
                        ))}
                        {selectedOption && <p>{selectedOption === slide.exercise.correctAnswer ? 'Correct!' : 'Incorrect'}</p>}
                    </div>
                )}
            </div>
        );
    };

    return (
        <div className="popup-content">
            <h2>{slides[currentSlide].title}</h2>
            {renderSlideContent()}
            <div className="navigation-buttons">
                <button onClick={goToPreviousSlide} disabled={currentSlide === 0}>Previous</button>
                <button onClick={goToNextSlide} disabled={currentSlide === slides.length - 1}>Next</button>
            </div>
        </div>
    );
};

export default PopUpContent;
