import React, { useState } from 'react';
import './PopUpContent.scss';

const PopUpContent = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const theorySlides = [
        {
            type: "Theory",
            title: "Introduction to Video Editing Software",
            content: "Learn about different types of video editing software, their interfaces, and key features."
        },
        {
            type: "Theory",
            title: "Basic Editing Techniques",
            content: "Explore the fundamentals of cutting, trimming, and sequencing clips to tell a story."
        },
        {
            type: "Theory",
            title: "Sound Design Basics",
            content: "Understand the importance of sound, how to add soundtracks, and balance audio in your edits."
        },
        {
            type: "Theory",
            title: "Color Correction Techniques",
            content: "Discover how to enhance your video's look with color correction and grading."
        },
        // Add more theory slides as needed
    ];

    const practicalSlides = [
        {
            type: "Practical",
            title: "Identify the Tools",
            content: "Match video editing tools with their functions.",
            exercise: {
                type: "match",
                items: [
                    { tool: "Razor", function: "Cutting clips" },
                    { tool: "Hand", function: "Navigating the timeline" },
                    // ... other tool-function pairs
                ]
            }
        },
        {
            type: "Practical",
            title: "Order the Steps",
            content: "Arrange the steps of a basic editing workflow in the correct sequence.",
            exercise: {
                type: "ordering",
                steps: ["Import footage", "Cut and trim clips", "Add transitions", "Export final video"]
            }
        },
        {
            type: "Practical",
            title: "Sound Balancing Quiz",
            content: "Choose the correct approach to balance different audio elements in a scene.",
            exercise: {
                type: "quiz",
                question: "How would you balance dialogue and background music?",
                options: ["Keep both at same level", "Dialogue louder than music", "Music louder than dialogue"],
                correctAnswer: "Dialogue louder than music"
            }
        },
        // Add more practical slides as needed
    ];



    const goToNextSlide = () => {
        if (currentSlide < slides.length - 1) {
            setCurrentSlide(currentSlide + 1);
        }
    };

    const goToPreviousSlide = () => {
        if (currentSlide > 0) {
            setCurrentSlide(currentSlide - 1);
        }
    };

    const renderSlide = () => {
        const slide = slides[currentSlide];
        return (
            <div>
                <h2>{slide.title}</h2>
                <p>{slide.content}</p>
            </div>
        );
    };

    return (
        <div className="popup-content">
            {renderSlide()}
            <div className="navigation-buttons">
                <button onClick={goToPreviousSlide} disabled={currentSlide === 0}>Previous</button>
                <button onClick={goToNextSlide} disabled={currentSlide === slides.length - 1}>Next</button>
            </div>
        </div>
    );
};

export default PopUpContent;
