import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const initialData = {
    questions: [
        { id: 'question-1', content: 'Kedy vznikol photoshop:', answer: '1988' },
        // pridajte ďalšie otázky podľa potreby
    ],
    options: ['1990', '2000', '2010', '2020'], // možnosti odpovedí
};

function App() {
    const [data, setData] = useState(initialData);

    const onDragEnd = (result) => {
        // ak sa položka nepresunula na platné miesto
        if (!result.destination) return;

        const { source, destination } = result;

        // presunúť odpoveď na nové miesto
        const newOptions = Array.from(data.options);
        newOptions.splice(source.index, 1);
        newOptions.splice(destination.index, 0, data.options[source.index]);

        setData({ ...data, options: newOptions });
    };

    const checkAnswers = () => {
        // skontrolujte, či sú odpovede správne
        const isCorrect = data.questions.every((q, i) => q.answer === data.options[i]);
        alert(isCorrect ? 'Odpovedali ste správne!' : 'Skúste to znova.');
    };

    return (
        <DragDropContext onDragEnd={onDragEnd}>
            <Droppable droppableId="droppable">
                {(provided) => (
                    <div {...provided.droppableProps} ref={provided.innerRef}>
                        {data.questions.map((q, i) => (
                            <div key={q.id}>
                                <span>{q.content}</span>
                                <Draggable draggableId={q.id} index={i}>
                                    {(provided) => (
                                        <div
                                            {...provided.draggableProps}
                                            {...provided.dragHandleProps}
                                            ref={provided.innerRef}
                                        >
                                            {q.answer}
                                        </div>
                                    )}
                                </Draggable>
                            </div>
                        ))}
                        {provided.placeholder}
                    </div>
                )}
            </Droppable>
            <Droppable droppableId="options">
                {(provided) => (
                    <div {...provided.droppableProps} ref={provided.innerRef}>
                        {data.options.map((option, i) => (
                            <Draggable key={option} draggableId={option} index={i}>
                                {(provided) => (
                                    <div
                                        {...provided.draggableProps}
                                        {...provided.dragHandleProps}
                                        ref={provided.innerRef}
                                    >
                                        {option}
                                    </div>
                                )}
                            </Draggable>
                        ))}
                        {provided.placeholder}
                    </div>
                )}
            </Droppable>
            <button onClick={checkAnswers}>Skontrolovať odpovede</button>
        </DragDropContext>
    );
}

export default App;
