import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable, DropResult } from 'react-beautiful-dnd';


const initialData = {
    questions: [
        { id: 'question-1', content: 'Kedy vznikol photoshop:', answer: '' },
        // pridajte ďalšie otázky podľa potreby
    ],
    options: ['1990', '1988', '2000'], // možnosti odpovedí
};

function App() {
    const [data, setData] = useState(initialData);

    const onDragEnd = (result: DropResult) => {
        // ak sa položka nepresunula na platné miesto
        if (!result.destination) return;

        const { source, destination } = result;

        // presunúť odpoveď na nové miesto
        const newOptions = Array.from(data.options);
        newOptions.splice(source.index, 1);

        const newQuestions = Array.from(data.questions);
        const questionIndex = Number(destination.droppableId);

        // ak už bola k otázke priradená odpoveď, vráť ju späť do možností
        if (newQuestions[questionIndex].answer) {
            newOptions.push(newQuestions[questionIndex].answer);
        }

        newQuestions[questionIndex].answer = data.options[source.index];

        setData({ questions: newQuestions, options: newOptions });
    };


    const checkAnswers = () => {
        // skontrolujte, či sú odpovede správne
        const isCorrect = data.questions.every((q) => q.answer === '1988');
        alert(isCorrect ? 'Odpovedali ste správne!' : 'Skúste to znova.');
    };

    return (
        <DragDropContext onDragEnd={onDragEnd}>
            {data.questions.map((q, i) => (
                <Droppable droppableId={String(i)} key={q.id}>
                    {(provided) => (
                        <div {...provided.droppableProps} ref={provided.innerRef}>
                            <span>{q.content}</span>
                            {q.answer && (
                                <div>
                                    {q.answer}
                                </div>
                            )}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            ))}
            <Droppable droppableId="options">
                {(provided) => (
                    <div {...provided.droppableProps} ref={provided.innerRef}>
                        {data.options.map((option, i) => (
                            <Draggable key={option} draggableId={option} index={i}>
                                {(provided) => (
                                    <div
                                        {...provided.draggableProps}
                                        {...provided.dragHandleProps}
                                        ref={provided.innerRef}
                                    >
                                        {option}
                                    </div>
                                )}
                            </Draggable>
                        ))}
                        {provided.placeholder}
                    </div>
                )}
            </Droppable>
            <button onClick={checkAnswers}>Skontrolovať odpovede</button>
        </DragDropContext>
    );
}

export default App;
