import React, { useState } from 'react';
import './PopUpContent.scss';

const PopUpContent = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const theorySlides = [
        {
            type: "Theory",
            title: "Introduction to Video Editing Software",
            content: "Learn about different types of video editing software, their interfaces, and key features."
        },
        {
            type: "Theory",
            title: "Basic Editing Techniques",
            content: "Explore the fundamentals of cutting, trimming, and sequencing clips to tell a story."
        },
        {
            type: "Theory",
            title: "Sound Design Basics",
            content: "Understand the importance of sound, how to add soundtracks, and balance audio in your edits."
        },
        {
            type: "Theory",
            title: "Color Correction Techniques",
            content: "Discover how to enhance your video's look with color correction and grading."
        },
        // Add more theory slides as needed
    ];



    const goToNextSlide = () => {
        if (currentSlide < slides.length - 1) {
            setCurrentSlide(currentSlide + 1);
        }
    };

    const goToPreviousSlide = () => {
        if (currentSlide > 0) {
            setCurrentSlide(currentSlide - 1);
        }
    };

    const renderSlide = () => {
        const slide = slides[currentSlide];
        return (
            <div>
                <h2>{slide.title}</h2>
                <p>{slide.content}</p>
            </div>
        );
    };

    return (
        <div className="popup-content">
            {renderSlide()}
            <div className="navigation-buttons">
                <button onClick={goToPreviousSlide} disabled={currentSlide === 0}>Previous</button>
                <button onClick={goToNextSlide} disabled={currentSlide === slides.length - 1}>Next</button>
            </div>
        </div>
    );
};

export default PopUpContent;
