import React, { useRef } from "react";
import "./Course.scss";

interface CourseProps {
    title: string;
    image: string;
    active?: boolean;
    onClick?: () => void;
}

const Course: React.FC<CourseProps> = (props) => {
    const courseRef = useRef<HTMLDivElement>(null);

    const handleRemove = () => {
        courseRef.current?.remove();
    };

    const { title, image, active } = props;

    return (
        <div
            className={`Course ${active ? 'active' : ''}`}
        >
            <div className={`image-container ${active ? 'active' : ''}`}>
                <img src={image} alt={title} />
            </div>
            <h2>{title}</h2>
            {active && <button onClick={handleRemove}>Remove</button>}
        </div>
    );
}

export default Course;
