import React from 'react';
import HeaderComponent from "../../components/PageNotFoundComponents/header/HeaderComponent";
import MainContent from "../../components/MainPageComponents/MainContent/MainContent";
import "./Main.scss";
import Second from "../../../src/components/MainPageComponents/Second/Second";
import Footer from "../../components/MainPageComponents/footer/footer"
import ImageText from "../../components/MainPageComponents/ImageText/ImageText";
import ImageAbout from "../../global/assets/media.svg"
import ImageImpressiveLearning from "../../global/assets/ImmersiveLearning.svg"

export default class MainPage extends React.Component {

    scrollToSection = (sectionId: string) => {
        const element = document.getElementById(sectionId);
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    render() {

        return (
            <>
                <div className={"Background"}>
                    <HeaderComponent scrollToSection={this.scrollToSection} />
                    <MainContent/>
                </div>
                <div className={"Background2"}>
                    <div id ={"about"}>
                        <h1 style={{
                            fontSize: '2.2rem',
                            textAlign: 'center',
                            marginBottom: '2rem',
                            color: 'darkorange',
                        }}>
                            About us
                        </h1>
                        <ImageText imageUrl={ImageAbout}
                                   text={"Embark on a transformative learning journey with our innovative multimedia education platform, where cutting-edge technology seamlessly blends with engaging content. Immerse yourself in a world of captivating videos, interactive quizzes, and dynamic educational materials, unlocking a whole new dimension of learning."}
                                   heading={"Unleash the Power of Immersive Learning"} textPosition={"right"} />
                        <ImageText imageUrl={ImageAbout}
                                   text={"Our platform goes beyond traditional learning methods, prioritizing active engagement and personalized experiences. Through interactive exercises, self-paced learning modules, and personalized feedback, you'll gain a deeper understanding of your subject matter."}
                                   heading={"Experience the Difference"} textPosition={"left"} />
                        <ImageText imageUrl={ImageAbout}
                                   text={"Ditch the monotony of traditional learning and embrace the transformative power of multimedia education. Discover how our interactive approach can make learning not only effective but also enjoyable, igniting your passion for knowledge."}
                                   heading={"Unlock Your Full Potential"} textPosition={"right"} />
                        <ImageText imageUrl={ImageAbout}
                                   text={"Embark on a journey of discovery and unlock your full learning potential. Sign up for our multimedia education platform today and experience the future of learning."}
                                   heading={"Join Us Today"} textPosition={"left"} />
                    </div>
                    <div id={"courses"}>
                        <Second/>
                    </div>
                </div>
                <div id={"contact"}>
                    <Footer/>
                </div>
            </>
        );
    }
}
