import React, { useState } from 'react';
import './Second.scss';
import Course from './Course/Course';
import MediaImage from "../../../global/assets/media.svg";
import MusicImage from "../../../global/assets/music2.svg";
import ImageCourseImage from "../../../global/assets/image_course.svg";
import CourseDetails from "./CourseDetails/CourseDetails";

interface SecondProps {
}

export const Second: React.FC<SecondProps> = props => {

    const coursesData = [
        { id: 1, title: "Media", image: MediaImage, article: "Media course article" },
        { id: 2, title: "Music", image: MusicImage, article: "Music course article" },
        { id: 3, title: "Image", image: ImageCourseImage, article: "Image course article" },
    ];

    const [selectedCourseId, setSelectedCourseId] = useState<number | null>(null);

    const handleCourseClick = (courseId: number) => {
        setSelectedCourseId(courseId);
    };

    const Visibility = true;

    const handleClose = () => {
        setSelectedCourseId(null);
    };

    const selectedCourse = coursesData.find((course) => course.id === selectedCourseId);

    return (
        <div className="MainCourses">
            <h1>Our courses</h1>
            <div className="Courses">
                {coursesData.map((course) => (
                    <Course
                        key={course.id}
                        title={course.title}
                        image={course.image}
                        onClick={() => handleCourseClick(course.id)}
                    ></Course>
                ))}
            </div>

            {Visibility && (
                <CourseDetails
                    title={selectedCourse.title}
                    article={selectedCourse.article}
                    onClose={handleClose}
                ></CourseDetails>
            )}
        </div>
    );
};

export default Second;
