import React, { useState } from 'react';
import "./PopUpContent.scss";

const PopUpContent = () => {
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [showAnswer, setShowAnswer] = useState(false);
    const [selectedAnswer, setSelectedAnswer] = useState("");

    const questions = [
        // Example question structure
        {
            videoUrl: "path_to_video_clip",
            questionText: "What type of color correction should be applied?",
            options: ["Warm tones", "Cool tones", "High contrast", "Black & White"],
            correctAnswer: "Warm tones"
        },
        // More questions...
    ];

    const handleAnswer = (option) => {
        setSelectedAnswer(option);
        setShowAnswer(true);
        // Later, you can add logic to move to the next question
    };

    return (
        <div className="game-container">
            <h3>Video Editing Challenge</h3>
            <video src={questions[currentQuestion].videoUrl} controls></video>
            <p>{questions[currentQuestion].questionText}</p>
            <div className="options-container">
                {questions[currentQuestion].options.map((option, index) => (
                    <button key={index} onClick={() => handleAnswer(option)}>
                        {option}
                    </button>
                ))}
            </div>
            {showAnswer && (
                <div className="feedback">
                    {selectedAnswer === questions[currentQuestion].correctAnswer
                        ? "Correct! Here's why..."
                        : "Incorrect. The correct answer is..."}
                </div>
            )}
        </div>
    );
};

export default PopUpContent;
