import React, { useState, useRef } from 'react';
import './Second.scss';
import Course from './Course/Course';
import MediaImage from "../../../global/assets/media.svg";
import MusicImage from "../../../global/assets/music2.svg";
import ImageCourseImage from "../../../global/assets/image_course.svg";
import CourseDetails from "./CourseDetails/CourseDetails";

interface SecondProps {
}

export const Second: React.FC<SecondProps> = props => {
    const popupRef = useRef<HTMLDivElement>(null); // corrected type for popupRef

    const coursesData = [
        { id: 1, title: "Media", image: MediaImage, article: "Media course article" },
        { id: 2, title: "Music", image: MusicImage, article: "Music course article" },
        { id: 3, title: "Image", image: ImageCourseImage, article: "Image course article" },
    ];

    const handleCourseClick = (courseId: number) => {
        const selectedCourse = coursesData.find((course) => course.id === courseId); // corrected type of courseId parameter

        if (selectedCourse) {
            setCourseDetails({ // updated handling of selectedCourse
                title: selectedCourse.title,
                article: selectedCourse.article,
            });

            if (popupRef.current) {
                popupRef.current.show(); // corrected call to show method
            }
        }
    };

    return (
        <div className="MainCourses">
            <h1>Our courses</h1>
            <div className="Courses">
                {coursesData.map((course) => (
                    <Course
                        key={course.id}
                        title={course.title}
                        image={course.image}
                        onClick={() => handleCourseClick(course.id)} // corrected type of onClick prop
                    ></Course>
                ))}
            </div>

            <CourseDetails
                ref={popupRef}
                visible={!!courseDetails} // corrected handling of courseDetails
                title={courseDetails?.title} // corrected type of title prop
                article={courseDetails?.article} // corrected type of article prop
            ></CourseDetails>
        </div>
    );
};

export default Second;
