import React, { useState } from 'react';
import './PopUpContent.scss';

interface TheorySlide {
    type: "Theory";
    title: string;
    content: string;
}

interface PracticalSlide {
    type: "Practical";
    title: string;
    content: string;
    exercise: any; // Define a more specific type for your exercises
}

interface ConclusionSlide {
    type: "Conclusion";
    title: string;
    content: string;
}

type Slide = TheorySlide | PracticalSlide | ConclusionSlide;

const PopUpContent = () => {
    const [currentSlide, setCurrentSlide] = useState<number>(0);
    const [selectedOption, setSelectedOption] = useState<string | null>(null);
    const [selectedAnswers, setSelectedAnswers] = useState<{ [question: string]: string }>({});
    const [draggedItem, setDraggedItem] = useState<string | null>(null);
    const [filledBlanks, setFilledBlanks] = useState<{ [key: string]: string }>({});
    const [matchingSelections, setMatchingSelections] = useState<{ [key: string]: string }>({});
    const [checkResult, setCheckResult] = useState<string | null>(null);

    const originalPairs = [
        { left: "Diegetic Sound", right: "Dialogue within a scene" },
        { left: "Non-Diegetic Sound", right: "Narrator's commentary" },
        { left: "Ambient Sound", right: "Background city noise" },
        { left: "Foley", right: "Artificially created sounds, like footsteps" },
        { left: "Sound Effects (SFX)", right: "Gunshots, explosions" },
        { left: "Background Music", right: "Music score enhancing mood" },
        { left: "Direct Sound", right: "Sounds captured by the primary microphone" }
    ];



    const theorySlides: TheorySlide[] = [
        {
            type: "Theory",
            title: "Introduction to Video Editing Software",
            content: "Learn about different types of video editing software, their interfaces, and key features..."
        },
        {
            type: "Theory",
            title: "Basic Editing Techniques",
            content: "Explore the fundamentals of cutting, trimming, and sequencing clips to tell a story..."
        },
        {
            type: "Theory",
            title: "Sound Design in Video Editing",
            content: "Understand the importance of sound, how to add soundtracks, and balance audio in your edits..."
        },
        {
            type: "Theory",
            title: "Color Correction and Grading",
            content: "Discover how to enhance your video's look with color correction and grading..."
        },
        // Add more theory slides as needed
    ];

    const practicalSlides: PracticalSlide[] = [
        {
            type: "Practical",
            title: "Video Software Features Quiz",
            content: "Test your knowledge on different video editing software features.",
            exercise: {
                type: "multiple-choice",
                questions: [
                    // Sample question
                    {
                        question: "Which software uses a Magnetic Timeline?",
                        options: ["Adobe Premiere Pro", "Final Cut Pro", "DaVinci Resolve"],
                        correctAnswer: "Final Cut Pro"
                    },
                    // Add more questions
                ]
            }
        },
        {
            type: "Practical",
            title: "Fill in the Blanks",
            content: "Drag the correct terms into the blanks.",
            exercise: {
                type: "drag-and-drop",
                text: ["The editing software ", "____", " is known for its ", "____", "."],
                blanks: { blank1: "Adobe Premiere Pro", blank2: "Magnetic Timeline" },
                options: ["Adobe Premiere Pro", "Final Cut Pro", "DaVinci Resolve", "Magnetic Timeline", "Color Correction"]
            }
        },
        {
            type: "Practical",
            title: "Sound Design Challenge",
            content: "Match the sound type with its correct example.",
            exercise: {
                type: "matching",
                pairs: originalPairs // Use the originalPairs array here
            }
        },
    ];


    const conclusionSlide: ConclusionSlide = {
        type: "Conclusion",
        title: "Summary of Key Concepts",
        content: "Recap of the key points covered in the course, emphasizing the importance of storytelling, technical skills, and creativity in video editing."
    };

    const slides: Slide[] = [...theorySlides, ...practicalSlides, conclusionSlide];

    const goToNextSlide = () => {
        setCurrentSlide(currentSlide + 1);
        setSelectedOption(null);
    };

    const goToPreviousSlide = () => {
        setCurrentSlide(currentSlide - 1);
        setSelectedOption(null);
    };

    const handleMultipleChoiceSelection = (question: string, option: string) => {
        setSelectedAnswers(prev => ({ ...prev, [question]: option }));
    };

    const isAnswerCorrect = (question: string, option: string) => {
        const currentExercise = (slides[currentSlide] as PracticalSlide).exercise;
        if (currentExercise.type === "multiple-choice") {
            const correctAnswer = currentExercise.questions.find((q: any) => q.question === question).correctAnswer;
            return option === correctAnswer;
        }
        return false;
    };

    const renderMultipleChoiceQuestions = (exercise: any) => {
        return (
            <div>
                {exercise.questions.map((question: any, index: number) => (
                    <div key={index} className="quiz-question">
                        <p>{question.question}</p>
                        {question.options.map((option: string, optIndex: number) => (
                            <button
                                key={optIndex}
                                onClick={() => handleMultipleChoiceSelection(question.question, option)}
                                className={selectedAnswers[question.question] === option ? 'selected' : ''}
                            >
                                {option}
                            </button>
                        ))}
                        {selectedAnswers[question.question] &&
                            <p className={isAnswerCorrect(question.question, selectedAnswers[question.question]) ? 'correct' : 'incorrect'}>
                                {isAnswerCorrect(question.question, selectedAnswers[question.question]) ? 'Correct answer!' : 'Incorrect answer.'}
                            </p>
                        }
                    </div>
                ))}
            </div>
        );
    };

    const handleDragStart = (item: string, fromBlank: boolean = false) => {
        if (fromBlank) {
            // If dragging from a blank, remove the item from the blank
            const updatedBlanks = { ...filledBlanks };
            for (const key in updatedBlanks) {
                if (updatedBlanks[key] === item) {
                    updatedBlanks[key] = "____"; // Reset the blank
                    break;
                }
            }
            setFilledBlanks(updatedBlanks);
        }
        setDraggedItem(item);
    };


    const handleDrop = (blankIndex: number) => {
        if (draggedItem) {
            const blankKey = `blank${blankIndex + 1}`; // Adjust index to match key structure
            setFilledBlanks(prev => ({ ...prev, [blankKey]: draggedItem }));
            setDraggedItem(null);
        }
    };


    const checkAnswers = () => {
        const currentSlideData = slides[currentSlide] as PracticalSlide;
        if (currentSlideData.type === "Practical" && currentSlideData.exercise.type === "drag-and-drop") {
            let allCorrect = true;
            for (const blankKey in currentSlideData.exercise.blanks) {
                if (currentSlideData.exercise.blanks[blankKey] !== filledBlanks[blankKey]) {
                    allCorrect = false;
                    break;
                }
            }
            setCheckResult(allCorrect ? "All answers are correct!" : "Some answers are incorrect.");
        }
    };

    const renderDragAndDropExercise = (exercise: any) => {
        return (
            <div>
                <div className="exercise-text">
                    {exercise.text.map((part: string, index: number) => (
                        part === "____" ?
                            <div
                                key={index}
                                className="blank"
                                onDrop={() => handleDrop(index)}
                                onDragOver={(e) => e.preventDefault()}
                                draggable
                                onDragStart={() => handleDragStart(filledBlanks[`blank${index + 1}`], true)}
                            >
                                {filledBlanks[`blank${index + 1}`] || part}
                            </div> :
                            <span key={index}>{part}</span>
                    ))}
                </div>
                <div className="options">
                    {exercise.options.map((option: string, index: number) => (
                        <div
                            key={index}
                            className="draggable-option"
                            draggable
                            onDragStart={() => handleDragStart(option)}
                        >
                            {option}
                        </div>
                    ))}
                </div>
                <button onClick={checkAnswers} className="check-answers-btn">
                    Check Answers
                </button>
                {checkResult && <div className="check-result">{checkResult}</div>}
            </div>
        );
    };

    const shuffleArray = (array: any[]) => {
        for (let i = array.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
        }
        return array;
    };

    const shuffledLeftOptions = shuffleArray(originalPairs.map(pair => pair.left));
    const shuffledRightOptions = shuffleArray(originalPairs.map(pair => pair.right));

    const handleMatchSelect = (left: string, right: string) => {
        setMatchingSelections(prev => ({
            ...prev,
            [left]: right
        }));
    };

/*    const checkIfMatchCorrect = (left: string, right: string, exercise: any) => {
        const correctPair = exercise.pairs.find((pair: any) => pair.left === left);
        return correctPair && correctPair.right === right;
    };
*/
    const checkIfMatchCorrect = () => {
        let allCorrect = true;

        for (const [left, right] of Object.entries(userSelections)) {
            const correctPair = originalPairs.find(pair => pair.left === left && pair.right === right);
            if (!correctPair) {
                allCorrect = false;
                break;
            }
        }

        setCheckResult(allCorrect ? "All answers are correct!" : "Some answers are incorrect.");
    };

    const handleSelect = (side: 'left' | 'right', option: string) => {
        // Logika pro spracovanie výberu
        // ...
    };


    const renderMatchingExercise = () => {
        return (
            <div className="matching-exercise-container">
                <div className="left-options">
                    {shuffledLeftOptions.map((option, index) => (
                        <button key={index} onClick={() => handleSelect('left', option)}>
                            {option}
                        </button>
                    ))}
                </div>
                <div className="right-options">
                    {shuffledRightOptions.map((option, index) => (
                        <button key={index} onClick={() => handleSelect('right', option)}>
                            {option}
                        </button>
                    ))}
                </div>
            </div>
        );
    };


    const renderExerciseContent = (exercise: any) => {
        switch (exercise.type) {
            case 'multiple-choice':
                return renderMultipleChoiceQuestions(exercise);
            case 'drag-and-drop':
                return renderDragAndDropExercise(exercise);
            case 'matching':
                return renderMatchingExercise(exercise);
            default:
                return null;
        }
    };

    const renderSlideContent = () => {
        const slide = slides[currentSlide];

        return (
            <div>
                <p>{slide.content}</p>
                {slide.type === 'Practical' && renderExerciseContent(slide.exercise)}
            </div>
        );
    };

    return (
        <div className="popup-content">
            <h2>{slides[currentSlide].title}</h2>
            {renderSlideContent()}
            <div className="navigation-buttons">
                <button onClick={goToPreviousSlide} disabled={currentSlide === 0}>Previous</button>
                <button onClick={goToNextSlide} disabled={currentSlide === slides.length - 1}>Next</button>
            </div>
        </div>
    );
};

export default PopUpContent;
