// Game.tsx
import React, { useState } from "react";
import { useDrag, useDrop } from "react-dnd";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import "./Game.scss";

interface Block {
    id: number;
    text: string;
    type: string;
    isDropped: boolean;
}

const BlockTypes = {
    TEXT: "text",
};

interface TextBlockProps {
    id: number;
    text: string;
    type: string;
    isDropped: boolean;
}

const TextBlock: React.FC<TextBlockProps> = ({ id, text, type, isDropped }) => {
    const [{ isDragging }, drag] = useDrag({
        type,
        item: { id, type },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    });

    return (
        <div
            ref={drag}
            className={`block ${isDragging ? "dragging" : ""} ${
                isDropped ? "dropped" : ""
            }`}
        >
            {text}
        </div>
    );
};

interface DropTargetProps {
    onDrop: (blockId: number) => void;
    accepts: string;
    children?: React.ReactNode; // Add this line to include children prop
}

const DropTarget: React.FC<DropTargetProps> = ({ onDrop, accepts, children }) => {
    const [{ isOver }, drop] = useDrop({
        accept: accepts,
        drop: (item: { id: number; type: string }) => onDrop(item.id),
        collect: (monitor) => ({
            isOver: !!monitor.isOver(),
        }),
    });

    return (
        <div ref={drop} className={`drop-target ${isOver ? "over" : ""}`}>
            {children}
        </div>
    );
};

const Game: React.FC = () => {
    const [blocks, setBlocks] = useState<Block[]>([
        { id: 1, text: "Text 1", type: BlockTypes.TEXT, isDropped: false },
        { id: 2, text: "Text 2", type: BlockTypes.TEXT, isDropped: false },
        { id: 3, text: "Text 3", type: BlockTypes.TEXT, isDropped: false },
    ]);

    const handleDrop = (blockId: number) => {
        setBlocks((prevBlocks) =>
            prevBlocks.map((block) =>
                block.id === blockId ? { ...block, isDropped: true } : block
            )
        );
    };

    const checkAnswers = () => {
        const correctAnswers = blocks.filter((block) => block.isDropped).length;
        alert(`You got ${correctAnswers} correct answers!`);
    };

    return (
        <div className="game-container">
            <DndProvider backend={HTML5Backend}>
                <div className="blocks-container">
                    {blocks.map((block) => (
                        <TextBlock
                            key={block.id}
                            id={block.id}
                            text={block.text}
                            type={block.type}
                            isDropped={block.isDropped}
                        />
                    ))}
                </div>

                <DropTarget accepts={BlockTypes.TEXT} onDrop={handleDrop}>
                    <div className="drop-area">Drop here</div>
                </DropTarget>

                <button onClick={checkAnswers}>Check Answers</button>
            </DndProvider>
        </div>
    );
};

export default Game;
