import React, { useState } from 'react';
import './PopUpContent.scss';

interface TheorySlide {
    type: "Theory";
    title: string;
    content: string;
}

interface ExerciseItem {
    question: string;
    options: string[];
    correctAnswer: string;
}

interface PracticalSlide {
    type: "Practical";
    title: string;
    content: string;
    exercise: {
        type: string;
        items: ExerciseItem[];
    };
}

interface ConclusionSlide {
    type: "Conclusion";
    title: string;
    content: string;
}


const PopUpContent = () => {
    const [currentSlide, setCurrentSlide] = useState(0);
    const [selectedOption, setSelectedOption] = useState<string | null>(null);

    const theorySlides = [
        {
            type: "Theory",
            title: "Introduction to Video Editing Software",
            content: `Video editing software is a cornerstone of film and media production. It allows editors to piece together clips, add effects, and create a cohesive story. Key features include a timeline for sequencing, cutting tools, effects libraries, and color grading capabilities. Popular software includes Adobe Premiere Pro, Final Cut Pro, and DaVinci Resolve. Understanding the interface and basic functionalities is crucial for aspiring video editors.`
        },
        {
            type: "Theory",
            title: "Basic Editing Techniques",
            content: `The art of editing is not just about cutting; it's about storytelling. Basic techniques include L-cuts and J-cuts, which allow audio and video to flow smoothly. Cut on action, matching scene cuts to the subject's movements, creates a seamless visual experience. Montage, an assembly of short clips to condense time and space, is another crucial technique. Understanding these basics is fundamental to creating engaging and meaningful video content.`
        },
        {
            type: "Theory",
            title: "Sound Design in Video Editing",
            content: `Sound design enhances the emotional impact and realism of video content. It involves the manipulation of audio elements like dialogue, background music, and sound effects. Key concepts include diegetic and non-diegetic sounds - sounds that are part of the story's world versus those added for dramatic effect. Balancing these elements ensures clarity and enhances the storytelling. The right sound design can elevate a video from good to great.`
        },
        {
            type: "Theory",
            title: "Color Correction and Grading",
            content: `Color correction and grading are vital for setting the mood and style of a video. Correction involves fixing color imbalances and exposure errors to achieve a natural look. Grading is the creative process of stylizing the footage's color palette. Tools like color wheels, curves, and LUTs (Look-Up Tables) are used. Understanding the color theory and its psychological impact is key to effective color grading.`
        },
        // ...additional slides can be added here...
    ];


    const practicalSlides = [
        {
            type: "Practical",
            title: "Identify the Tools",
            content: "Match video editing tools with their functions.",
            exercise: {
                type: "match",
                items: [
                    { tool: "Razor", function: "Cutting clips" },
                    { tool: "Hand", function: "Navigating the timeline" },
                    // ... other tool-function pairs
                ]
            }
        },
        {
            type: "Practical",
            title: "Order the Steps",
            content: "Arrange the steps of a basic editing workflow in the correct sequence.",
            exercise: {
                type: "ordering",
                steps: ["Import footage", "Cut and trim clips", "Add transitions", "Export final video"]
            }
        },
        {
            type: "Practical",
            title: "Sound Balancing Quiz",
            content: "Choose the correct approach to balance different audio elements in a scene.",
            exercise: {
                type: "quiz",
                question: "How would you balance dialogue and background music?",
                options: ["Keep both at same level", "Dialogue louder than music", "Music louder than dialogue"],
                correctAnswer: "Dialogue louder than music"
            }
        },
        // Add more practical slides as needed
    ];


    const conclusionSlide = {
        type: "Conclusion",
        title: "Summary of Key Concepts",
        content: "This is a summary of the key points covered in the course."
    };

    const slides: (TheorySlide | PracticalSlide | ConclusionSlide)[] = [...theorySlides, ...practicalSlides, conclusionSlide];

    const handleOptionSelect = (option: string) => {
        setSelectedOption(option);
    };

    const goToNextSlide = () => {
        setCurrentSlide(currentSlide + 1);
        setSelectedOption(null);
    };

    const goToPreviousSlide = () => {
        setCurrentSlide(currentSlide - 1);
        setSelectedOption(null);
    };

    const renderSlideContent = () => {
        const slide = slides[currentSlide];
        const isQuiz = slide.type === 'Practical' && (slide as PracticalSlide).exercise?.type === 'quiz';

        return (
            <div>
                <p>{slide.content}</p>
                {isQuiz
                    && (
                        <div>
                            {(slide as PracticalSlide).exercise.options.map((option: string, index: number) => (
                                <button key={index} onClick={() => handleOptionSelect(option)}>
                                    {option}
                                </button>
                            ))}
                            {selectedOption && <p>{selectedOption === (slide as PracticalSlide).exercise.correctAnswer ? 'Correct!' : 'Incorrect'}</p>}
                        </div>
                    )}
            </div>
        );
    };

    return (
        <div className="popup-content">
            <h2>{slides[currentSlide].title}</h2>
            {renderSlideContent()}
            <div className="navigation-buttons">
                <button onClick={goToPreviousSlide} disabled={currentSlide === 0}>Previous</button>
                <button onClick={goToNextSlide} disabled={currentSlide === slides.length - 1}>Next</button>
            </div>
        </div>
    );

};

export default PopUpContent;
