import React, { useState } from 'react';
import './CourseDetails.scss';

interface CourseDetailsProps {
    title: string;
    article: string;
}

const CourseDetails: React.FC<CourseDetailsProps> = ({ title, article }) => {
    const [visible, setVisible] = useState(false);

    const handleClose = () => {
        setVisible(false);
    };

    return (
        <div className={"CourseDetails"} style={{ display: visible ? "block" : "none" }} onClose={handleClose}>
            <h2>{title}</h2>
            <p>{article}</p>
            <button onClick={handleClose}>Close</button>
        </div>
    );
};

export default CourseDetails;
