// Game.tsx
import React, { useState } from 'react';
import { useDrop, useDrag } from 'react-dnd';
import './Game.scss';
import TextBlock from './TextBlock'; // Updated import statement
import { BlockTypes } from './Types';

interface GameProps {
    blocks: {
        id: number;
        text: string;
        type: BlockTypes;
    }[];
}

const Game: React.FC<GameProps> = ({ blocks }) => {
    const [currentDropId, setCurrentDropId] = useState<number | null>(null);

    const [, drop] = useDrop({
        accept: BlockTypes.TEXT,
        drop: (item: { id: number }) => setCurrentDropId(item.id),
    });

    return (
        <div className="game-container">
            <div ref={drop} className="drop-area">
                Drop here
            </div>

            <div className="blocks-container">
                {blocks.map((block) => (
                    <TextBlock key={block.id} {...block} isDropped={block.id === currentDropId} />
                ))}
            </div>
        </div>
    );
};

export default Game;
