import React, { useState } from 'react';
import './PopUpContent.scss';

interface TheorySlide {
    type: "Theory";
    title: string;
    content: string;
}

interface PracticalSlide {
    type: "Practical";
    title: string;
    content: string;
    exercise: any; // Define a more specific type for your exercises
}

interface ConclusionSlide {
    type: "Conclusion";
    title: string;
    content: string;
}

type Slide = TheorySlide | PracticalSlide | ConclusionSlide;

const PopUpContent = () => {
    const [currentSlide, setCurrentSlide] = useState<number>(0);
    const [selectedOption, setSelectedOption] = useState<string | null>(null);

    const theorySlides: TheorySlide[] = [
        {
            type: "Theory",
            title: "Introduction to Video Editing Software",
            content: "Learn about different types of video editing software, their interfaces, and key features..."
        },
        {
            type: "Theory",
            title: "Basic Editing Techniques",
            content: "Explore the fundamentals of cutting, trimming, and sequencing clips to tell a story..."
        },
        {
            type: "Theory",
            title: "Sound Design in Video Editing",
            content: "Understand the importance of sound, how to add soundtracks, and balance audio in your edits..."
        },
        {
            type: "Theory",
            title: "Color Correction and Grading",
            content: "Discover how to enhance your video's look with color correction and grading..."
        },
        // Add more theory slides as needed
    ];

    const practicalSlides: PracticalSlide[] = [
        {
            type: "Practical",
            title: "Video Software Features Quiz",
            content: "Test your knowledge on different video editing software features.",
            exercise: {
                type: "multiple-choice",
                questions: [
                    // Sample question
                    {
                        question: "Which software uses a Magnetic Timeline?",
                        options: ["Adobe Premiere Pro", "Final Cut Pro", "DaVinci Resolve"],
                        correctAnswer: "Final Cut Pro"
                    },
                    // Add more questions
                ]
            }
        },
        {
            type: "Practical",
            title: "Match the Editing Technique",
            content: "Drag each editing technique to its correct definition.",
            exercise: {
                type: "drag-and-drop",
                items: [
                    // Sample item
                    { option: "L-Cut", targets: ["Audio transitions before video"] },
                    // Add more items
                ]
            }
        },
        {
            type: "Practical",
            title: "Sound Design Challenge",
            content: "Match the sound type with its correct example.",
            exercise: {
                type: "matching",
                pairs: [
                    // Sample pair
                    { left: "Diegetic Sound", right: "Dialogue within a scene" },
                    // Add more pairs
                ]
            }
        },
        // Add more practical slides as needed
    ];


    const conclusionSlide: ConclusionSlide = {
        type: "Conclusion",
        title: "Summary of Key Concepts",
        content: "Recap of the key points covered in the course, emphasizing the importance of storytelling, technical skills, and creativity in video editing."
    };

    const slides: Slide[] = [...theorySlides, ...practicalSlides, conclusionSlide];


    const handleOptionSelect = (option: string) => {
        setSelectedOption(option);
    };

    const goToNextSlide = () => {
        setCurrentSlide(currentSlide + 1);
        setSelectedOption(null);
    };

    const goToPreviousSlide = () => {
        setCurrentSlide(currentSlide - 1);
        setSelectedOption(null);
    };

    const handleMultipleChoiceSelection = (question: string, option: string) => {
        setSelectedAnswers(prev => ({ ...prev, [question]: option }));
    };

    const isAnswerCorrect = (question: string, option: string) => {
        const currentExercise = (slides[currentSlide] as PracticalSlide).exercise;
        if (currentExercise.type === "multiple-choice") {
            const correctAnswer = currentExercise.questions.find((q: any) => q.question === question).correctAnswer;
            return option === correctAnswer;
        }
        return false;
    };

    const renderMultipleChoiceQuestions = (exercise: any) => {
        return (
            <div>
                {exercise.questions.map((question: any, index: number) => (
                    <div key={index} className="quiz-question">
                        <p>{question.question}</p>
                        {question.options.map((option: string, optIndex: number) => (
                            <button
                                key={optIndex}
                                onClick={() => handleMultipleChoiceSelection(question.question, option)}
                                className={selectedAnswers[question.question] === option ? 'selected' : ''}
                            >
                                {option}
                            </button>
                        ))}
                        {selectedAnswers[question.question] &&
                            <p className={isAnswerCorrect(question.question, selectedAnswers[question.question]) ? 'correct' : 'incorrect'}>
                                {isAnswerCorrect(question.question, selectedAnswers[question.question]) ? 'Correct answer!' : 'Incorrect answer.'}
                            </p>
                        }
                    </div>
                ))}
            </div>
        );
    };

    const renderDragAndDropExercise = (exercise: any) => {
        // Simplified version; for full implementation consider using react-dnd
        return (
            <div>
                {exercise.items.map((item: any, index: number) => (
                    <p key={index}>Drag {item.option} to {item.targets.join(", ")}</p>
                    // Implement drag and drop functionality here
                ))}
            </div>
        );
    };

    const renderMatchingExercise = (exercise: any) => {
        return (
            <div>
                {exercise.pairs.map((pair: any, index: number) => (
                    <div key={index}>
                        <span>{pair.left}</span> matches with <span>{pair.right}</span>
                        // Implement matching functionality here
                    </div>
                ))}
            </div>
        );
    };

    const renderExerciseContent = (exercise: any) => {
        switch (exercise.type) {
            case 'multiple-choice':
                return renderMultipleChoiceQuestions(exercise);
            case 'drag-and-drop':
                return renderDragAndDropExercise(exercise);
            case 'matching':
                return renderMatchingExercise(exercise);
            default:
                return null;
        }
    };

    const renderSlideContent = () => {
        const slide = slides[currentSlide];

        return (
            <div>
                <p>{slide.content}</p>
                {slide.type === 'Practical' && renderExerciseContent(slide.exercise)}
            </div>
        );
    };

    return (
        <div className="popup-content">
            <h2>{slides[currentSlide].title}</h2>
            {renderSlideContent()}
            <div className="navigation-buttons">
                <button onClick={goToPreviousSlide} disabled={currentSlide === 0}>Previous</button>
                <button onClick={goToNextSlide} disabled={currentSlide === slides.length - 1}>Next</button>
            </div>
        </div>
    );
};

export default PopUpContent;
