import React, { forwardRef, useImperativeHandle, useState } from 'react';
import './CourseDetails.scss';

interface CourseDetailsProps {
    title: string;
    article: string;
}

const CourseDetails = forwardRef((props: CourseDetailsProps, ref) => {
    const [isVisible, setVisible] = useState(props.visible);

    useImperativeHandle(ref, () => ({
        show() {
            setVisible(true);
        },
        hide() {
            setVisible(false);
        },
    }));

    const handleClose = () => {
        setVisible(false);
    };

    return (
        <div className="CourseDetails" style={{ display: isVisible ? 'block' : 'none' }}>
            <h2>{props.title}</h2>
            <p>{props.article}</p>
            <button onClick={handleClose}>Close</button>
        </div>
    );
});

export default CourseDetails;
