// TextBlock.tsx
import React from 'react';

interface TextBlockProps {
    id: number;
    text: string;
    type: string;
    isDropped: boolean;
    isAnimating: boolean;
}

const TextBlock: React.FC<TextBlockProps> = ({ id, text, type, isDropped, isAnimating }) => {
    return (
        <div
            style={{
                opacity: isDropped ? 0.5 : 1,
                fontWeight: isAnimating ? 'bold' : 'normal',
                cursor: 'move',
            }}
        >
            {text}
        </div>
    );
};

export default TextBlock;
