import React, { useState } from 'react';
import './PopUpContent.scss';

const PopUpContent = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const slides = [
        {
            title: "Introduction to Media Production",
            content: "Content for Introduction to Media Production..."
        },
        {
            title: "Basics of Video Editing",
            content: "Content for Basics of Video Editing..."
        },
        {
            title: "Advanced Video Editing Techniques",
            content: "Content for Advanced Video Editing Techniques..."
        },
        {
            title: "Practical Exercise - Basic Editing",
            content: "Content for Practical Exercise - Basic Editing..."
        },
        {
            title: "Practical Exercise - Advanced Editing",
            content: "Content for Practical Exercise - Advanced Editing..."
        },
        {
            title: "Key Takeaways",
            content: "Content for Key Takeaways..."
        },
        {
            title: "Further Resources",
            content: "Content for Further Resources..."
        }
    ];

    const nextSlide = () => {
        if (currentSlide < slides.length - 1) {
            setCurrentSlide(currentSlide + 1);
        }
    };

    const prevSlide = () => {
        if (currentSlide > 0) {
            setCurrentSlide(currentSlide - 1);
        }
    };

    return (
        <div className="popup-content">
            <h3>{slides[currentSlide].title}</h3>
            <p>{slides[currentSlide].content}</p>
            <div className="button-container">
                <button onClick={prevSlide} disabled={currentSlide === 0}>Previous</button>
                <button onClick={nextSlide} disabled={currentSlide === slides.length - 1}>Next</button>
            </div>
        </div>
    );
};

export default PopUpContent;
