import React, { useState } from 'react';
import './PopUpContent.scss';

const PopUpContent = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const slides = [
        { type: "Theory", title: "Introduction to Media Production", content: "Content about Media Production..." },
        { type: "Theory", title: "Basics of Video Editing", content: "Content about Video Editing Basics..." },
        // Add more Theory slides...

        { type: "Practical", title: "Practical Exercise: Editing Techniques", content: "Content about practical editing techniques..." },
        // Add more Practical slides...

        { type: "Summary", title: "Summary of Key Concepts", content: "Key points from the course..." }
    ];


    const goToNextSlide = () => {
        if (currentSlide < slides.length - 1) {
            setCurrentSlide(currentSlide + 1);
        }
    };

    const goToPreviousSlide = () => {
        if (currentSlide > 0) {
            setCurrentSlide(currentSlide - 1);
        }
    };

    const renderSlide = () => {
        const slide = slides[currentSlide];
        return (
            <div>
                <h2>{slide.title}</h2>
                <p>{slide.content}</p>
            </div>
        );
    };

    return (
        <div className="popup-content">
            {renderSlide()}
            <div className="navigation-buttons">
                <button onClick={goToPreviousSlide} disabled={currentSlide === 0}>Previous</button>
                <button onClick={goToNextSlide} disabled={currentSlide === slides.length - 1}>Next</button>
            </div>
        </div>
    );
};

export default PopUpContent;
