import React, { useState } from 'react';
import './PopUpContent.scss';

const PopUpContent = () => {
    const [currentSlide, setCurrentSlide] = useState(0);

    const slides = [
        {
            title: "Introduction to Video Editing",
            content: "Learn the basics of video editing: cutting, trimming, and sequencing."
        },
        {
            title: "Advanced Editing Techniques",
            content: "Explore advanced techniques: color correction, transitions, and special effects."
        },
        {
            title: "Sound Design in Editing",
            content: "Understand the role of sound: adding soundtracks, effects, and balancing audio."
        },
        // Add more slides as needed
    ];

    const nextSlide = () => {
        if (currentSlide < slides.length - 1) {
            setCurrentSlide(currentSlide + 1);
        }
    };

    const prevSlide = () => {
        if (currentSlide > 0) {
            setCurrentSlide(currentSlide - 1);
        }
    };

    return (
        <div className="popup-content">
            <h3>{slides[currentSlide].title}</h3>
            <p>{slides[currentSlide].content}</p>
            <button onClick={prevSlide} disabled={currentSlide === 0}>Previous</button>
            <button onClick={nextSlide} disabled={currentSlide === slides.length - 1}>Next</button>
        </div>
    );
};

export default PopUpContent;
