import React, { useState } from "react";
import "./Second.scss";
import Course from "./Course/Course";
import MediaImage from "../../../global/assets/media.svg";
import MusicImage from "../../../global/assets/music2.svg";
import ImageCourseImage from "../../../global/assets/image_course.svg";

interface SecondContentProps {}

const Second: React.FC<SecondContentProps> = (props) => {
    // Replace the following array with your actual array of course data
    const coursesData = [
        { id: 1, title: "Media", image: MediaImage },
        { id: 2, title: "Music", image: MusicImage },
        { id: 3, title: "Image", image: ImageCourseImage },
    ];

    const [activeCourse, setActiveCourse] = useState<number | null>(null);

    const handleCourseClick = (courseId: number) => {
        setActiveCourse(courseId === activeCourse ? null : courseId);
    };

    return (
        <div className={"MainCourses"}>
            <h1>Our courses</h1>
            <div className={"Courses"}>
                {coursesData.map((course) => (
                    <Course
                        key={course.id}
                        title={course.title}
                        image={course.image}
                        active={course.id === activeCourse}
                        onClick={() => handleCourseClick(course.id)}
                    />
                ))}
            </div>
        </div>
    );
};

export default Second;
